var bigInt = require('./bigInt');
var dictionary = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
var base = dictionary.length;

function generate(){
    var destination = '';
    var bigint = bigInt.generate();
    generate.source = bigInt.source;
    while(!bigint.isZero())
    {
        remainder = bigint.mod(base);
        destination=dictionary.charAt(remainder)+destination;

        bigint = bigint.divide(base);
    }
    return destination;
}

function characters(characters){
    if(characters){
        dictionary = characters;
        base = dictionary.length;
    }
    return dictionary;
}

module.exports = generate;
module.exports.generate = generate;
module.exports.characters = characters;
